class t{static foldCode(e){const o=e.target;if(o.classList.contains("md-code-fold-btn")||o.classList.contains("md-code-fold-icon")){const c=o.closest("code");if(!c)return;c.classList.toggle("collapsed")}}static addCodeFoldingHook(){document.querySelectorAll(".markdown-body").forEach(o=>{o.removeEventListener("click",t.foldCode),o.addEventListener("click",t.foldCode)})}static removeCodeFoldingHook(){document.querySelectorAll(".markdown-body").forEach(o=>{o.removeEventListener("click",t.foldCode)})}}export{t as m};
